INSERT INTO "rooms" VALUES (9, "Southeast Tower Grounds", "The southeast corner of Garin's Tower sits in a perpetual fog. The fog", "is very thick and and hard to breath almost giving you a sense that you", "are going to choke. The noises around you suggest you aren't the only", "one feeling the discomfort. Those whatever it is appears used to it as if", "it lives on this part of the tower grounds.", "1:8|7:9", 1, 0, 0, 0);
INSERT INTO "rooms" VALUES (10, "Southern Tower Grounds", "The south side of Garin's Tower faces the Black Forrest. If it weren't for", "the light shining on the trees on the edge of the forrest, you wouldn't be", "able to see anything of it. Not only does no light come from within the ", "forrest, but no sound escapes the darkness either.", "*", "3:9|7:10", 1, 0, 0, 0);
