INSERT INTO "rooms" VALUES (13, "Southwest Tower Grounds", "The southwest corner of Garin's Tower has a huge crater in the ground. It", "isn't known what caused the crater, only that it has been here since before", "recorded history. The base of the crater pulsates as if it was leaving", "heart. It is unknown if anyone has actually been down to investigate it. ", "If they have, it's never been reported. Maybe they never returned.", "1:13|3:12", 1, 0, 0, 0);
INSERT INTO "rooms" VALUES (14, "Western Tower Grounds", "The west side of Garin's Tower is dominated by the tower itself as the ", "entrance of the tower is on this side. Flames rise from wall hanging ", "torches that never seem to burnout. This side of the tower almost looks", "like a face with flame for eyes and the gate itself for a mouth. Every few", "minutes a booming low noise sounds almost like a horn.", "1:14|5:13", 1, 0, 0, 0);
INSERT INTO "rooms" VALUES (15, "Tower Entrance", "Garin's Tower entrance has a mammoth gate that is in the shape of a mouth.", "A very low booming noise comes from the tower gate every few minutes. The", "sound is low enough to rattle you to the bone. The flickering flame from ", "the torches on the wall cast moving shadows. The face that the flames", "and gate form on the tower are very unsettling.", "1:15|3:17|5:14", 1, 0, 0, 0);
