INSERT INTO "rooms" VALUES (17, "Inner Tower Entrance", "Garin's Tower inner entrance is a massive room that houses the gate itself.", "There are guard barricks here though no guards to be seen. Torches line ", "the walls. The room is blue and continues south. There is a large red ", "stair case leading up to the north. The booming noise her coming from here", "from outside is no longer heard from within the tower.", "1:18|5:19|7:17", 1, 0, 0, 0);
INSERT INTO "rooms" VALUES (18, "Red Grand Stair", "This grand stair is blood red. There are long red drapes hanging from ", "every wall. The railing is polished silver and doesn't look like it has ", "ever tarnished even though it's clearly thousands of years old. The", "drapes flutter a bit as if there was a breeze flowing through the room,", "but you don't feel any breeze.", "3:26|5:18", 1, 0, 0, 0);
